import React, { useState } from "react";
import { fetchContributionData } from "../services/fetchCommits";
import "../styles/styles.css"; // Ensure correct CSS file import

const Dashboard = () => {
    const [selectedOrg, setSelectedOrg] = useState("HWAI-CST");
    const [selectedUser, setSelectedUser] = useState("");
    const [selectedRepo, setSelectedRepo] = useState("");
    const [contributions, setContributions] = useState([]);

    const ORGANIZATIONS = {
        "HWAI-CST": {
            users: ["ArulTEG", "ArunPrem_APP", "deepesh-tech", "Mukul Kumar", "suneelkumar02", "dhorinaresh"],
            repositories: ["LobbyClientUI-R1", "LobbyAdminServerAPI-R1", "LobbyHeartbeatAutomationUI-R1", "HubspotIntegrationAPI-R1", "LobbyMiddlewareAPI-R1", "LobbyAdminPanelUI-R1", "LobbyServerAPI-R1"]
        },
        "HWAI-ProductIntel": {
            users: ["ankit_sde@hwai", "ArulTEG", "bindu-e", "Kritika-sharma12", "Mukul Kumar", "Promod Kr Verma", "satyajeet-HealthworksAI", "Rajeev Rawat", "vibhore-go", "vidushi-chaudhary"],
            repositories: ["ChatbotEOCUI-R1", "ChatbotEOCAPI-R1", "ChatbotEOCModel-R1", "PlanComparisonUI-R4", "PlanComparisonAPI-R4"]
        }
    };

    const handleFetchData = async () => {
        if (!selectedUser || !selectedRepo) {
            alert("Please select a user and a repository!");
            return;
        }
        const data = await fetchContributionData(selectedOrg, selectedUser, selectedRepo);
        setContributions(data);
    };

    return (
        <div className="dashboard-container">
            <h1>GitHub Contributions Dashboard</h1>

            {/* Organization Filter */}
            <label>Select Organization:</label>
            <select value={selectedOrg} onChange={(e) => setSelectedOrg(e.target.value)} className="dropdown">
                {Object.keys(ORGANIZATIONS).map(org => (
                    <option key={org} value={org}>{org}</option>
                ))}
            </select>

            {/* User Filter */}
            <label>Select User:</label>
            <select value={selectedUser} onChange={(e) => setSelectedUser(e.target.value)} className="dropdown">
                <option value="">-- Select User --</option>
                {ORGANIZATIONS[selectedOrg].users.map(user => (
                    <option key={user} value={user}>{user}</option>
                ))}
            </select>

            {/* Repository Filter */}
            <label>Select Repository:</label>
            <select value={selectedRepo} onChange={(e) => setSelectedRepo(e.target.value)} className="dropdown">
                <option value="">-- Select Repository --</option>
                {ORGANIZATIONS[selectedOrg].repositories.map(repo => (
                    <option key={repo} value={repo}>{repo}</option>
                ))}
            </select>

            {/* Execute Button */}
            <button className="execute-btn" onClick={handleFetchData}>Execute</button>

            {/* Display Contribution Data */}
            <table className="contribution-table">
                <thead>
                    <tr>
                        <th>User</th>
                        <th>Repository</th>
                        <th>Commits</th>
                    </tr>
                </thead>
                <tbody>
                    {contributions.map((entry, index) => (
                        <tr key={index}>
                            <td>{entry.user}</td>
                            <td>{entry.repository}</td>
                            <td>{entry.count}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
