export async function fetchContributionData() {
  const USERS = [
    "ArulTEG",
    "ArunPrem_APP",
    "deepesh-tech",
    "Mukul Kumar",
    "suneelkumar02",
    "dhorinaresh",
  ];
  const REPOSITORIES = [
    "LobbyClientUI-R1",
    "LobbyAdminServerAPI-R1",
    "LobbyHeartbeatAutomationUI-R1",
    "HubspotIntegrationAPI-R1",
    "LobbyMiddlewareAPI-R1",
    "LobbyAdminPanelUI-R1",
    "LobbyServerAPI-R1",
  ];
  const ORG_NAME = "HWAI-CST";
  const TOKEN = process.env.REACT_APP_GITHUB_PAT;

  let contributions = [];

  for (const repo of REPOSITORIES) {
    const url = `https://api.github.com/repos/${ORG_NAME}/${repo}/commits`;

    try {
      const response = await fetch(url, {
        headers: { Authorization: `token ${TOKEN}` },
      });

      const data = await response.json();
      console.log(`ðŸ“¢ API Response for ${repo}:`, data);

      if (response.status !== 200) {
        console.error(`âŒ Error Fetching: ${repo}`, data.message);
        continue;
      }

      data.forEach((commit) => {
        if (commit.author && USERS.includes(commit.author.login)) {
          const username = commit.author.login;
          const existingEntry = contributions.find(
            (entry) => entry.user === username && entry.repository === repo
          );

          if (existingEntry) {
            existingEntry.count += 1;
          } else {
            contributions.push({
              user: username,
              repository: repo,
              count: 1,
            });
          }
        }
      });
    } catch (error) {
      console.error(`âŒ API Error for ${repo}:`, error);
    }
  }

  // Calculate total contributions
  const totalCommits = contributions.reduce(
    (sum, entry) => sum + entry.count,
    0
  );

  // Convert to percentage
  contributions = contributions.map((entry) => ({
    ...entry,
    percentage: ((entry.count / totalCommits) * 100).toFixed(2),
  }));

  console.log("âœ… Final Contribution Data:", contributions);

  return contributions;
}
