// src/components/ContributionChart.js
import React, { useState } from "react";
import { fetchContributions } from "../api";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const ContributionChart = () => {
  const [contributions, setContributions] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadContributions = async () => {
    setLoading(true);
    const repo = "HWAI-CST/LobbyMiddlewareAPI-R1"; // Update the repo as needed
    const data = await fetchContributions(repo);
    setContributions(data);
    setLoading(false);
  };

  return (
    <div>
      <h2>GitHub Contributions</h2>
      <button onClick={loadContributions}>Load Contributions</button>
      {loading ? <p>Loading...</p> : null}

      {contributions.length > 0 && (
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={contributions}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="login" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="contributions" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default ContributionChart;
