import React, { useState, useEffect } from "react";
import { fetchContributionData } from "../services/fetchCommits";
import "../styles/styles.css";

const Dashboard = () => {
    const [selectedOrg, setSelectedOrg] = useState("");
    const [selectedRepo, setSelectedRepo] = useState("");
    const [selectedUser, setSelectedUser] = useState("");
    const [contributions, setContributions] = useState([]);

    // Organizations and Repositories Mapping
    const ORGANIZATIONS = {
        "HWAI-MarketIntel": ["EnrollmentTrendsUI-R4", "EnrollmentTrendsAPI-R4", "CompetitorAnalysisUI-R1"],
        "HWAI-NetworkIntel": ["PNCOneToManyUI-R1", "PNCOneToManyAPI-R1", "NetworkAdequacyUI-R1"],
        "HWAI-ProductIntel": ["ChatbotEOCUI-R1", "ChatbotEOCAPI-R1", "PlanComparisonUI-R4"],
    };

    // Static User List
    const USERS = ["ArulTEG", "ArunPrem_APP", "deepesh-tech", "Mukul Kumar", "suneelkumar02"];

    // Fetch Contributions
    const handleFetchData = async () => {
        if (!selectedOrg || !selectedRepo || !selectedUser) {
            alert("Please select Organization, Repository, and User!");
            return;
        }

        console.log(`Fetching data for ${selectedOrg}/${selectedRepo} - ${selectedUser}`);
        const data = await fetchContributionData(selectedOrg, selectedRepo, selectedUser);

        if (!data || data.length === 0) {
            alert("No contributions found for the selected criteria.");
            setContributions([]);
        } else {
            setContributions(data);
        }
    };

    return (
        <div className="dashboard-container">
            <h1>GitHub Contributions Dashboard</h1>

            {/* Filter Section (Horizontal Alignment) */}
            <div className="filters-container">
                <select onChange={(e) => setSelectedOrg(e.target.value)} value={selectedOrg}>
                    <option value="">-- Select Organization --</option>
                    {Object.keys(ORGANIZATIONS).map(org => (
                        <option key={org} value={org}>{org}</option>
                    ))}
                </select>

                <select onChange={(e) => setSelectedRepo(e.target.value)} value={selectedRepo} disabled={!selectedOrg}>
                    <option value="">-- Select Repository --</option>
                    {selectedOrg && ORGANIZATIONS[selectedOrg].map(repo => (
                        <option key={repo} value={repo}>{repo}</option>
                    ))}
                </select>

                <select onChange={(e) => setSelectedUser(e.target.value)} value={selectedUser}>
                    <option value="">-- Select User --</option>
                    {USERS.map(user => (
                        <option key={user} value={user}>{user}</option>
                    ))}
                </select>

                <button onClick={handleFetchData}>Execute</button>
            </div>

            {/* Contribution Results Table */}
            {contributions.length > 0 && (
                <table className="contributions-table">
                    <thead>
                        <tr>
                            <th>Commit ID</th>
                            <th>Message</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {contributions.map(commit => (
                            <tr key={commit.commit_id}>
                                <td>{commit.commit_id}</td>
                                <td>{commit.message}</td>
                                <td>{commit.date}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Dashboard;
