export const fetchRepositories = async (organization) => {
  try {
    const response = await fetch(
      `https://api.github.com/orgs/${organization}/repos?per_page=100`,
      {
        headers: {
          Authorization: `token ${process.env.REACT_APP_GITHUB_PAT}`, // Use PAT from .env
        },
      }
    );
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Error fetching repositories:", error);
    return [];
  }
};

export const fetchContributions = async (organization) => {
  try {
    const repositories = await fetchRepositories(organization);
    const contributions = [];

    for (const repo of repositories) {
      const response = await fetch(
        `https://api.github.com/repos/${organization}/${repo.name}/contributors`,
        {
          headers: {
            Authorization: `token ${process.env.REACT_APP_GITHUB_PAT}`, // Use PAT from .env
          },
        }
      );
      const data = await response.json();
      contributions.push(...data);
    }

    return contributions;
  } catch (error) {
    console.error("Error fetching contributions:", error);
    return [];
  }
};
