import React, { useState, useEffect } from "react";
import { fetchContributionData } from "../services/fetchCommits";

const Dashboard = () => {
  // Users & Repositories List
  const USERS = [
    "ArulTEG",
    "ArunPrem_APP",
    "deepesh-tech",
    "Mukul Kumar",
    "suneelkumar02",
    "dhorinaresh",
  ];

  // State for selected filters
  const [selectedUser, setSelectedUser] = useState(""); // Selected User
  const [selectedRepo, setSelectedRepo] = useState(""); // Selected Repository
  const [contributions, setContributions] = useState([]); // Contribution Data

  // Fetch data when "Execute" button is clicked
  const handleExecute = async () => {
    const data = await fetchContributionData();

    // Filter Data based on selected User & Repo
    const filteredData = data.filter(
      (entry) =>
        (selectedUser ? entry.user === selectedUser : true) &&
        (selectedRepo ? entry.repository === selectedRepo : true)
    );

    setContributions(filteredData);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>GitHub Contributions Dashboard</h2>

      {/* Filters Section */}
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {/* User Dropdown */}
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
        >
          <option value="">All Users</option>
          {USERS.map((user) => (
            <option key={user} value={user}>
              {user}
            </option>
          ))}
        </select>

        {/* Repository Dropdown */}
        <select
          value={selectedRepo}
          onChange={(e) => setSelectedRepo(e.target.value)}
        >
          <option value="">All Repositories</option>
          {REPOSITORIES.map((repo) => (
            <option key={repo} value={repo}>
              {repo}
            </option>
          ))}
        </select>

        {/* Execute Button */}
        <button onClick={handleExecute} style={{ padding: "5px 10px" }}>
          Execute
        </button>
      </div>

      {/* Display Contribution Data */}
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>User</th>
            <th>Repository</th>
            <th>Commits</th>
            <th>Percentage</th>
          </tr>
        </thead>
        <tbody>
          {contributions.length > 0 ? (
            contributions.map((entry, index) => (
              <tr key={index}>
                <td>{entry.user}</td>
                <td>{entry.repository}</td>
                <td>{entry.count}</td>
                <td>{entry.percentage}%</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: "center" }}>
                No Data Available
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
