import React, { useState, useEffect } from "react";
import { fetchContributionData } from "../services/fetchCommits";
import "../styles/Styles.css"; // Ensure correct CSS file import

const Dashboard = () => {
    const [selectedOrg, setSelectedOrg] = useState("HWAI-CST");
    const [contributions, setContributions] = useState([]);

    const ORGANIZATIONS = ["HWAI-CST", "HWAI-ProductIntel"];

    useEffect(() => {
        async function fetchData() {
            const data = await fetchContributionData(selectedOrg);
            setContributions(data);
        }
        fetchData();
    }, [selectedOrg]); // Fetch new data when the organization changes

    return (
        <div className="dashboard-container">
            <h1>GitHub Contributions Dashboard</h1>

            {/* Organization Filter */}
            <label>Select Organization:</label>
            <select 
                value={selectedOrg} 
                onChange={(e) => setSelectedOrg(e.target.value)}
                className="dropdown"
            >
                {ORGANIZATIONS.map(org => (
                    <option key={org} value={org}>{org}</option>
                ))}
            </select>

            {/* Display Contribution Data */}
            <table className="contribution-table">
                <thead>
                    <tr>
                        <th>User</th>
                        <th>Repository</th>
                        <th>Commits</th>
                        <th>Percentage</th>
                    </tr>
                </thead>
                <tbody>
                    {contributions.map((entry, index) => (
                        <tr key={index}>
                            <td>{entry.user}</td>
                            <td>{entry.repository}</td>
                            <td>{entry.count}</td>
                            <td>{entry.percentage}%</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
