export async function fetchContributionData(selectedOrg, selectedRepo, selectedUser) {
    const ORGANIZATIONS = {
        "HWAI-MarketIntel": {
            repositories: ["EnrollmentTrendsUI-R4", "EnrollmentTrendsAPI-R4", "CompetitorAnalysisUI-R1", "CompetitorAnalysisAPI-R1"]
        },
        "HWAI-NetworkIntel": {
            repositories: ["PNCOneToManyUI-R1", "PNCOneToManyAPI-R1", "NetworkAdequacyUI-R1", "NetworkAdequacyAPI-R1", "NPISearchUI-R4", "ProviderNetworkComparisonUI-R4", "ProviderNetworkComparisonAPI-R4", "NPISearchAPI-R4"]
        },
        "HWAI-XAI": {
            repositories: ["ProductScorecardUI-R1", "BenefitSimulatorUI-R3.1", "BenefitSimulatorAPI-R3.1", "BenefitSimulatorAPI-R3", "ProductScorecardAPI-R1", "BenefitSimulatorUI-R3"]
        },
        "HWAI-ProductIntel": {
            repositories: ["ChatbotEOCUI-R1", "ChatbotEOCAPI-R1", "ChatbotEOCModel-R1", "PlanComparisonUI-R4", "PlanComparisonAPI-R4"]
        },
        "HWAI-CST": {
            repositories: ["LobbyClientUI-R1", "LobbyAdminServerAPI-R1", "LobbyHeartbeatAutomationUI-R1", "HubspotIntegrationAPI-R1", "LobbyMiddlewareAPI-R1", "LobbyAdminPanelUI-R1", "LobbyServerAPI-R1"]
        }
    };

    const USERS = [
        "ArulTEG", "ArunPrem_APP", "deepesh-tech", "Mukul Kumar",
        "suneelkumar02", "dhorinaresh", "ankit_sde@hwai", "bindu-e",
        "Kritika-sharma12", "Promod Kr Verma", "satyajeet-HealthworksAI",
        "Rajeev Rawat", "vibhore-go", "vidushi-chaudhary"
    ];

    if (!selectedOrg || !ORGANIZATIONS[selectedOrg]) {
        console.error("❌ Invalid Organization Selected");
        return [];
    }

    if (!selectedRepo || !ORGANIZATIONS[selectedOrg].repositories.includes(selectedRepo)) {
        console.error("❌ Invalid Repository Selected");
        return [];
    }

    if (!USERS.includes(selectedUser)) {
        console.error("❌ Invalid User Selected");
        return [];
    }

    const TOKEN = process.env.REACT_APP_GITHUB_PAT;
    const oneYearAgo = new Date();
    oneYearAgo.setFullYear(oneYearAgo.getFullYear() - 1);
    const sinceDate = oneYearAgo.toISOString();

    let contributions = [];
    const url = `https://api.github.com/repos/${selectedOrg}/${selectedRepo}/commits?since=${sinceDate}`;

    try {
        const response = await fetch(url, {
            headers: { Authorization: `token ${TOKEN}` }
        });

        if (!response.ok) {
            console.error(`❌ Failed to fetch data for ${selectedRepo}:`, response.statusText);
            return [];
        }

        const data = await response.json();

        data.forEach(commit => {
            if (commit.author && commit.author.login === selectedUser) {
                const username = commit.author.login;
                const existingEntry = contributions.find(entry => entry.user === username);

                if (existingEntry) {
                    existingEntry.count += 1;
                } else {
                    contributions.push({
                        user: username,
                        repository: selectedRepo,
                        count: 1
                    });
                }
            }
        });

    } catch (error) {
        console.error(`❌ Error fetching commits for ${selectedRepo}:`, error);
    }

    return contributions;
}
